import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import './App.css';
import SightCard from './components/SightCard';
import ZoneButtons from './components/ZoneButtons';
import LoadingSpinner from './components/LoadingSpinner';
import ErrorMessage from './components/ErrorMessage';

function App() {
  const [sights, setSights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedZone, setSelectedZone] = useState('');

  const zones = ['中山', '信義', '仁愛', '中正', '安樂', '七堵', '暖暖'];

  const loadZone = async (zone) => {
    setLoading(true);
    setError(null);
    setSelectedZone(zone);

    try {
      const response = await fetch(`/SightAPI?zone=${zone}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setSights(data);
    } catch (err) {
      setError(`資料載入失敗：${err.message}`);
      setSights([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App bg-light min-vh-100">
      <div className="container py-4">
        <h1 className="mb-4 text-center">基隆景點瀏覽器</h1>
        
        <ZoneButtons 
          zones={zones} 
          onZoneSelect={loadZone}
          selectedZone={selectedZone}
        />

        {loading && <LoadingSpinner />}
        
        {error && <ErrorMessage message={error} />}
        
        {!loading && !error && sights.length > 0 && (
          <div className="row">
            {sights.map((sight, index) => (
              <div key={index} className="col-12 col-md-6 col-lg-4 mb-4">
                <SightCard sight={sight} index={index} />
              </div>
            ))}
          </div>
        )}

        {!loading && !error && sights.length === 0 && selectedZone && (
          <div className="text-center text-muted mt-4">
            <p>該區域暫無景點資料</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
